const path = require('path');
require('dotenv').config({path: path.join(__dirname, '../.env')});

const nodemailer = require('nodemailer');

const transporter = nodemailer.createTransport({
    host: 'smtp.zoho.eu',
    port: 465,
    secure: true, //ssl
    auth: {
        user: nodeMailerUser,
        pass: nodeMailerPass
    }
})

// // send contact form submitted
async function sendContactForm (name, email, subject, phone, message) {
    const date = dayjs().format('MMM Do YYYY');

    let mailOptions = {
        // from: nodeMailerUser, // sender address (who sends)
        from: { // from using address object
            name: 'Greenscapes Gardening',
            address: nodeMailerUser
        },
        to: email, // list of receivers (who receives)
        cc: nodeMailerUser,
        subject: `We have received your enquiry`, // Subject line
        replyTo: email,
        html: `<b>${date}</b> <p>Email: ${email}</p> <p>Name: ${name}</p> <p>Phone: ${phone}</p> <p>Message: ${message}</p>` // html body
    };

    return transporter.sendMail(mailOptions)
    .then(res => {
        console.log(res);
    })
    .catch(error => {
        console.log(error);
    })
}


module.exports = {
    sendContactForm,
}
