// send application result regardless of belongsTo using different templateId's
function sendApplicationResult ( _id, subJoinText ) {

    const apiInstance = new SibApiV3Sdk.SMTPApi();
    let sendSmtpEmail = new SibApiV3Sdk.SendSmtpEmail();
    const template = {
        'approved': 2,
        'approved and upgraded to VIP': 2,
        'declined': 3,
        'approved for cashback': 28,
        'approved for cashback and upgraded to VIP': 28
    }
    sendSmtpEmail.sender = {
        name: 'eWalletBooster',
        email: 'support@ewalletbooster.com'
    }
    sendSmtpEmail.replyTo = {email: 'support@ewalletbooster.com'};
    sendSmtpEmail.tags = ['application'];

    Application.findById(_id)
    .then(a => {
        const vipStatus = getVipRequestText(subJoinText);
        sendSmtpEmail.to = [{
            email: a.accountEmailAddress,
            name: a.firstName
        }];
        if (a.belongsTo) {
            sendSmtpEmail.templateId = template[subJoinText];
            sendSmtpEmail.params = {
                FNAME: a.firstName,
                EMAIL: a.accountEmailAddress,
                ACCOUNTID: a.accountId,
                BRAND: a.brand,
                CURRENCY: a.accountCurrency,
                SUBJOINTEXT: subJoinText,
                VIPUPGRADETEXT: vipStatus
            }
            apiInstance.sendTransacEmail(sendSmtpEmail)
            .then(() => {
                return ({success: true, msg: 'Email sent'});       
            })
            .catch(err => {
                return err;
            })
        } else {
            Application.findById(a._id)
            .then(application => {
                Promise.all([application, crypto.randomBytes(20)])
                .then(([application, buffer]) => {
                    const token = buffer.toString('hex');
                    Application.findByIdAndUpdate(application._id, 
                        {applicationToken: token, applicationExpires: Date.now() + 86400000},
                        {upsert: true, new: true}
                    )
                    .then(() => {
                        sendSmtpEmail.templateId = subJoinText === 'declined' ? 3 : 4;
                        sendSmtpEmail.params = {
                            FNAME: a.firstName,
                            EMAIL: a.accountEmailAddress,
                            ACCOUNTID: a.accountId,
                            BRAND: a.brand,
                            CURRENCY: a.accountCurrency,
                            SUBJOINTEXT: subJoinText,
                            VIPUPGRADETEXT: vipStatus,
                            ID: token
                        }
                        apiInstance.sendTransacEmail(sendSmtpEmail)
                        .then(() => {
                            return ({success: true, msg: 'Email sent'});       
                        })
                        .catch(err => {
                            return err;
                        })
                    })
                    .catch(err => {
                        return err;
                    })
                })
                .catch(err => {
                    return err;
                })
            })
            .catch(err => {
                return err;
            })
        }
    })
    .catch(err => {
        return err;
    })
}
