            name: firstName
        }];
        sendSmtpEmail.replyTo = {email: 'support@ewalletbooster.com'};
        sendSmtpEmail.templateId = 13;
        sendSmtpEmail.tags = ['stats'];
        sendSmtpEmail.params = {
            FNAME: firstName,
            MONTH: monthId,
            PERIOD: periodId,
            ACCOUNTID: accountId,
            BRAND: brand,
            TRANSFERS: transfers,
            CASHBACK: cashback,
            RATE: rate
        }
        apiInstance.sendTransacEmail(sendSmtpEmail)
        .then(() => {
            return ({success: true, msg: 'Email sent'});       
        })
        .catch(err => {
            return err;
        })
    })
    .catch(err => {
