// contacts

// create new contact
function createNewContact ({ email, firstName, lastName, mobileNumber }) {
    const apiInstance = new SibApiV3Sdk.ContactsApi();
    let createContact = new SibApiV3Sdk.CreateContact();
    createContact.email = email;
    createContact.attributes = {
        FIRSTNAME: firstName,
        LASTNAME: lastName,
        PHONENUMBER: mobileNumber
    }
    createContact.listIds = [3];
    apiInstance.createContact(createContact)
    .then(() => {
        return ({ success: true, msg: 'User added' });   
    })
    .catch(() => {
        // if contact already exists - update it
        let updateContact = new SibApiV3Sdk.UpdateContact();
        updateContact.attributes = {
            FIRSTNAME: firstName,
            LASTNAME: lastName,
            PHONENUMBER: mobileNumber
        }
        updateContact.listIds = [3];
        updateContact.unlinkListIds = [8];
        apiInstance.updateContact(email, updateContact)
        .then(() => {
            return ({ success: true, msg: 'User updated' });   
        })
        .catch(e => e)
    })
}

